-- Volcando estructura para procedimiento db_snr.USP_Login_Usuario
DELIMITER //
CREATE PROCEDURE `USP_Login_Usuario`(
	IN `usuario` VARCHAR(50),
	IN `clave` VARCHAR(50)
)
BEGIN
SELECT u.Usuario_id,p.Apellido,p.Nombre, u.NombreUsuario,u.Nivel,u.Estado
	FROM tb_usuario as u
	INNER JOIN tb_persona as p
	ON u.Persona_id = p.Persona_id
	WHERE (u.NombreUsuario = usuario) AND u.Clave = clave;
END//
DELIMITER ;

